INSERT INTO `sin_perjanjian_kerjasamas` (`id`, `no_registrasi`, `tgl_surat`, `nomor_surat`, `judul`, `isian_surat`, `nomor_order_id`, `klasifikasi_id`, `masa_aktif`, `is_locked`, `is_draft`, `distribusikan_at`, `created_at`, `updated_at`, `created_by`, `updated_by`, `jenis_surat_id`, `kepada_id`, `lembaga_mitra`, `logo_mitra`, `nomor_surat_mitra`, `nama_pejabat_mitra`, `jabatan_pejabat_mitra`, `file_lampiran`, `has_lampiran`, `is_atas_nama`, `is_paraf_enabled`, `file_isian_surat`, `deleted_at`) VALUES
('526a4057-ce0c-4cd2-b68a-85ba36673344',	'2109.0001',	'2021-09-07',	'',	'PENGEMBANGAN PRODUK ONLINE PLATFORM SHARING ASSET PENDUKUNG PEMBELAJARAN JARAK JAUH',	'<p style=\"text-align: justify; line-height: 1.1;\">Pada hari ini, <strong>Rabu</strong> tanggal <strong>dua puluh empat</strong> bulan <strong>Maret</strong> tahun <strong>d</strong><strong>ua </strong><strong>r</strong><strong>ibu </strong><strong>d</strong><strong>ua </strong><strong>p</strong><strong>uluh</strong> <strong>satu (24 -</strong><strong> </strong><strong>0</strong><strong>3 -2021)</strong> bertempat di <strong>Universitas Pertamina</strong><strong> </strong>yang bertanda tangan di bawah ini:</p>\r\n<ol style=\"text-align: justify;\">\r\n<li><strong> </strong><strong> Dr. Wawan Gunawan A. Kadir,MS </strong><strong>, </strong>bertindak dalam jabatannya selaku Wakil Rektor III Bidang Penelitian, Pengembangan dan Kerjasama berdasarkan Surat Keputusan Dewan Pengurus Yayasan Pertamina Nomor : 0040/UP-R/SK/HK.01/III/2021 tentang Pengangkatan Wakil Rektor Universitas Pertamina, dari dan oleh karnanya bertindak untuk dan atas nama Rektor serta UNIVERSITAS PERTAMINA, berkedudukan di Jalan Teuku Nyak Arief, Simprug, Jakarta Selatan 12220, yang selanjutnya disebut <strong>PIHAK </strong><strong>PERTAMA</strong>.</li>\r\n<li><strong> </strong><strong>Rita Hastuti, M.Pd </strong><strong>, </strong>bertindak dalam jabatannya selaku Kepala SMA N 8 Jakarta berdasarkan SK Nomor: 1023 Tahun 2019 tanggal 27 September 2019 tentang Pengangkatan Kepala SMA N 8 Jakarta, dari dan oleh karenanya bertindak untuk dan atas nama Kepala SMA N 8 Jakarta, berkedudukan di Jalan Taman Bukit Duri No. 2 RW 12, Bukit Duri, Kecamatan Tebet, Kota Jakarta Selatan, DKI Jakarta 12840 yang selanjutnya disebut sebagai <strong>PIHAK </strong><strong>KEDUA</strong>.</li>\r\n</ol>\r\n<p style=\"text-align: justify; line-height: 1.1;\"><strong>PIHAK PERTAMA</strong> dan <strong>PIHAK KEDUA</strong> yang selanjutnya secara bersama-sama disebut <strong>PARA PIHAK</strong> menerangkan terlebih dahulu:</p>\r\n<ol style=\"text-align: justify;\">\r\n<li>Bahwa <strong>PIHAK PERTAMA</strong> adalah perguruan tinggi swasta yang mempunyai tugas dan fungsi menyelenggarakan pendidikan, penelitian, dan pengabdian kepada masyarakat;</li>\r\n<li>Bahwa <strong>PIHAK KEDUA</strong> adalah Sekolah Menegah Atas Negeri yang mempunyai tugas dan fungsi menyelenggarakan pendidikan;</li>\r\n</ol>\r\n<p style=\"text-align: justify; line-height: 1.1;\">Dengan memperhatikan ketentuan peraturan dan perundang-undangan sebagai berikut:</p>\r\n<ol style=\"text-align: justify;\">\r\n<li>Undang-Undang Nomor 20 Tahun 2003, tentang Sistem Pendidikan Nasional.</li>\r\n<li>Undang-Undang Nomor 12 Tahun 2012, tentang Pendidikan Tinggi.</li>\r\n<li>Peraturan Pemerintah Nomor 04 Tahun 2014, tentang Penyelenggaraan Pendidikan Tinggi dan Pengelolaan Perguruan Tinggi.</li>\r\n<li>Peraturan Presiden nomor 8 tahun 2012, tentang KKNI.</li>\r\n<li>Peraturan Menteri Pendidikan dan Kebudayaan Republik Indonesia Nomor 3 Tahun 2020, tentang Standar Nasional Pendidikan Tinggi.</li>\r\n</ol>\r\n<p style=\"text-align: justify; line-height: 1.1;\">Berdasarkan hal-hal tersebut di atas, <strong>PARA PIHAK</strong> dengan ini setuju dan sepakat untuk mengikatkan diri dalam Perjanjian Kerja Sama Pengembangan produk <em>Online Platform Sharing Asset </em>Pendukung Pembelajaran Jarak Jauh dengan ketentuan dan syarat-syarat sebagai berikut:</p>\r\n<p style=\"text-align: justify; line-height: 1;\"><!-- pagebreak --></p>\r\n<p style=\"text-align: center; line-height: 1.1;\"><strong>Pasal 1</strong></p>\r\n<p style=\"text-align: center; line-height: 1.1;\"><strong>LINGKUP PERJANJIAN KERJA SAMA</strong></p>\r\n<p style=\"text-align: justify; line-height: 1.1;\"><strong>PARA PIHAK</strong> telah sepakat dan setuju untuk mengadakan perjanjian kerja sama untuk mengembangkan produk <em>Online Platform Sharing Asset </em>Pendukung Pembelajaran Jarak Jauh dengan ketentuan:</p>\r\n<ul style=\"text-align: justify;\">\r\n<li><strong>PIHAK PERTAMA </strong>menunjuk Tim Pelaksana yang terdiri dari dosen dan mahasiswa untuk mengembangkan produk yang dimaksud.</li>\r\n<li><strong>PIHAK KEDUA</strong> menunjuk Tim Pelaksana yang terdiri dari siswa dan guru pendamping.</li>\r\n<li>Tim Pelaksana dari <strong>PIHAK PERTAMA</strong> memberikan pendampingan dalam pengembangan produk tersebut.</li>\r\n</ul>\r\n<p style=\"text-align: center; line-height: 1.1;\"><!-- pagebreak --></p>\r\n<p style=\"text-align: center; line-height: 1.1;\"><strong>&nbsp;</strong><strong>Pasal </strong><strong>2</strong></p>\r\n<p style=\"text-align: center; line-height: 1.1;\"><strong>HAK DAN </strong><strong>KEWAJIBAN </strong><strong>PARA PIHAK</strong></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><strong>PIHAK PERTAMA</strong> mempunyai hak dan kewajiban untuk:</li>\r\n</ul>\r\n<ol>\r\n<li style=\"list-style-type: none;\">\r\n<ol style=\"text-align: justify;\">\r\n<li>Menunjuk dosen dan mahasiswa sebagai Tim Pelaksana untuk melakukan pengembangan produk yang dijelaskan pada Pasal 1.</li>\r\n<li>Memberikan pendampingan kepada para siswa dari <strong>PIHAK KEDUA</strong> terkait pengembangan produk pada Pasal 1.</li>\r\n<li>Berhak untuk menentukan besaran dana pengembangan dan mekanisme pendampingan.</li>\r\n</ol>\r\n</li>\r\n</ol>\r\n<ul style=\"text-align: justify;\">\r\n<li><strong>PIHAK KEDUA</strong> mempunyai hak dan kewajiban untuk:</li>\r\n</ul>\r\n<ol>\r\n<li style=\"list-style-type: none;\">\r\n<ol style=\"text-align: justify;\">\r\n<li>Menunjuk siswa dan guru pendamping untuk melakukan pengembangan produk yang dijelaskan pada Pasal 1.</li>\r\n<li>Mengikuti program pendampingan yang dipersiapkan oleh <strong>PIHAK PERTAMA</strong>.</li>\r\n</ol>\r\n</li>\r\n</ol>\r\n<p style=\"text-align: center; line-height: 1.1;\"><!-- pagebreak --></p>\r\n<p style=\"text-align: center; line-height: 1.1;\"><strong>Pasal </strong><strong>3</strong></p>\r\n<p style=\"text-align: center; line-height: 1.1;\"><strong>KEPEMILIKAN HASIL</strong></p>\r\n<ul style=\"text-align: justify;\">\r\n<li>Semua penemuan dalam bentuk apapun yang terjadi dalam pelaksanaan perjanjian kerja sama ini adalah menjadi milik <strong>PARA PIHAK.</strong></li>\r\n<li>Penggunaan produk pada Pasal 1 adalah berdasarkan persetujuan oleh dari <strong>PARA PIHAK.</strong></li>\r\n<li>Apabila produk yang dihasilkan dari perjanjian kerja sama ini dikomersialisasikan, maka sistem bagi hasil usaha akan diatur dalam perjanjian yang akan dibuat oleh antara<strong> PARA PIHAK.</strong></li>\r\n</ul>\r\n<p style=\"text-align: center; line-height: 1.1;\"><!-- pagebreak --></p>\r\n<p style=\"text-align: center; line-height: 1.1;\"><strong>Pasal </strong><strong>4</strong></p>\r\n<p style=\"text-align: center; line-height: 1.1;\"><strong>BIAYA</strong></p>\r\n<p style=\"text-align: justify; line-height: 1.1;\">Segala biaya yang timbul sebagai akibat dari pelaksanaan Perjanjian Kerja Sama ini dibebankan kepada <strong>PARA PIHAK </strong>dengan besaran yang disesuikan dengan kemampuan<strong> PARA PIHAK.</strong></p>\r\n<p style=\"line-height: 1.1; text-align: center;\"><!-- pagebreak --></p>\r\n<p style=\"line-height: 1.1; text-align: center;\"><strong>&nbsp;</strong><strong>Pasal </strong><strong>5</strong></p>\r\n<p style=\"text-align: center; line-height: 1.1;\"><strong>JANGKA WAKTU</strong></p>\r\n<p style=\"text-align: justify; line-height: 1.1;\">Perjanjian kerja sama ini berlaku selama 2 (dua) tahun terhitung sejak tanggal ditandatanganinya Perjanjian kerja sama ini oleh <strong>PARA PIHAK</strong> sampai dengan tanggal <strong>dua puluh empat</strong> bulan <strong>Maret</strong> tahun 2023 (24-03-2023) dan dapat diperpanjang berdasarkan kesepakatan tertulis dari <strong>PARA PIHAK</strong>.</p>\r\n<p style=\"line-height: 1.1; text-align: center;\"><!-- pagebreak --></p>\r\n<p style=\"line-height: 1.1; text-align: center;\"><strong>&nbsp;</strong><strong>Pasal </strong><strong>6</strong></p>\r\n<p style=\"text-align: center; line-height: 1.1;\"><strong>PENGAKHIRAN KERJASAMA</strong></p>\r\n<p style=\"text-align: justify; line-height: 1.1;\">Perjanjian ini dapat diakhiri oleh <strong>PARA PIHAK</strong> dalam kondisi sebagai berikut :</p>\r\n<ul style=\"text-align: justify;\">\r\n<li style=\"line-height: 1.1;\">Salah satu pihak menyatakan tidak mampu untuk melaksanakan kerja sama ini, maka pihak yang menyatakan tidak mampu melanjutkan kerjasama ini berkewajiban memberitahukan kepada pihak yang lain dalam waktu 30 (tiga puluh) hari sebelumnya, atau atas kesepakatan <strong>PARA PIHAK</strong>.</li>\r\n<li style=\"line-height: 1.1;\">Apabila pada saat Perjanjian kerja sama ini berakhir atau diputuskan terdapat kewajiban yang belum dapat diselesaikan, maka ketentuan dalam perjanjian ini tetap berlaku sampai diselesaikannya kewajiban tersebut, paling lambat 3 (tiga) bulan sejak perjanjian ini berakhir.</li>\r\n</ul>\r\n<p style=\"text-align: center; line-height: 1;\"><!-- pagebreak --></p>\r\n<p style=\"text-align: center; line-height: 1.1;\"><strong>Pasal </strong><strong>7</strong></p>\r\n<p style=\"text-align: center; line-height: 1.1;\"><strong>PENYELESAIAN </strong><strong>PERSELISIHAN</strong></p>\r\n<p style=\"text-align: justify; line-height: 1.1;\">Dalam hal terjadi perbedaan penafsiran dalam pelaksanaan perjanjian ini, <strong>PARA PIHAK</strong> akan menyelesaikan secara musyawarah untuk mufakat dan apabila tidak tercapai kata mufakat, akan diselesaikan sesuai dengan ketentuan peraturan perundang-undangan.</p>\r\n<p style=\"text-align: center; line-height: 1.1;\"><!-- pagebreak --></p>\r\n<p style=\"text-align: center; line-height: 1.1;\"><strong>Pasal </strong><strong>8</strong></p>\r\n<p style=\"text-align: center; line-height: 1.1;\"><strong>KERAHASIAAN</strong></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><strong>PARA PIHAK</strong> sepakat untuk saling bertukar data dan informasi mengenai hal-hal yang berhubungan dengan pelaksanaan perjanjian ini dan yang semata-mata hanya digunakan untuk kepentingan yang berhubungan dengan maksud dan tujuan perjanjian</li>\r\n<li>Kecuali dalam rangka pelaksanaan suatu ketentuan peraturan perundang-undangan yang berlaku, kedua belah <strong>PIHAK</strong> sepakat untuk menjaga kerahasiaan seluruh data dan informasi sebagaimana dimaksud ayat (1) Pasal ini tidak akan memberikannya kepada pihak manapun diluar perjanjian kerja sama ini tanpa persetujuan tertulis dari <strong>P</strong><strong>ARA P</strong><strong>IHAK</strong></li>\r\n</ul>\r\n<p style=\"line-height: 1.1; text-align: center;\"><!-- pagebreak --></p>\r\n<p style=\"line-height: 1.1; text-align: center;\"><strong><!-- pagebreak --></strong><strong>Pasal </strong><strong>9</strong></p>\r\n<p style=\"text-align: center; line-height: 1.1;\"><strong>KEADAAN MEMAKSA/<em>FORCE MAJEURE</em></strong></p>\r\n<ul style=\"text-align: justify;\">\r\n<li>Keadaan <em>force majeure</em> adalah segala keadaan atau peristiwa yang terjadi di luar kekuasaan <strong>PARA PIHAK</strong>, seperti bencana alam, sabotase, pemogokkan, huru-hara, epidemik, pandemik, kebakaran, banjir, gempa bumi, perang, keputusan pemerintah atau instansi yang berwenang atau keadaan kerusakan jaringan listrik, kerusakan sistem dan komunikasi dari masing-masing <strong>PIHAK</strong> dan/atau pihak ketiga yang jasanya dimanfaatkan oleh satu pihak, yang menghalangi secara langsung atau tidak langsung untuk terlaksananya perjanjian ini.</li>\r\n<li>Setiap kejadian yang bersifat <em>force majeure</em> harus diberitahukan kepada <strong>PIHAK</strong> lainnya paling lambat 14 (empat belas) hari kalender sejak terjadinya <strong>FORCE MAJEURE </strong>dengan surat pemberitahuan yang disertai dengan keterangan resmi dari pejabat pemerintah setempat yang berwenang. Dalam hal terjadinya satu atau beberapa kejadian atau peristiwa yang menyebabkan pelaksanaan Perjanjian Kerja sama ini menjadi terlambat atau tidak dapat dilakukan sama sekali, maka segala kerugian yang timbul menjadi tanggung jawab masing-masing <strong>PIHAK</strong> dan hal ini tidak dapat dijadikan alasan oleh salah satu <strong>PIHAK</strong> untuk meminta ganti rugi terhadap <strong>PIHAK</strong> lainnya dan/atau memutuskan perjanjian ini.</li>\r\n<li>Keadaan <em>force majeure </em>yang menyebabkan keterlambatan pelaksanaan perjanjian ini, baik sebagian maupun seluruhnya tidak merupakan alasan untuk pengakhiran atau pembatalan Perjanjian, akan tetapi hanya merupakan keadaan yang menangguhkan perjanjian sampai dengan keadaan <em>force majeure</em></li>\r\n</ul>\r\n<p style=\"line-height: 1.1; text-align: center;\"><!-- pagebreak --></p>\r\n<p style=\"line-height: 1.1; text-align: center;\"><strong>&nbsp;</strong><strong>Pasal </strong><strong>10</strong></p>\r\n<p style=\"text-align: center; line-height: 1.1;\"><strong>KORESPONDENSI</strong></p>\r\n<p style=\"text-align: justify; line-height: 1.1;\">Semua surat menyurat atau pemberitahuan atau pernyataan-pernyataan atau persetujuan-persetujuan yang wajib dan perlu dilakukan oleh salah satu <strong>PIHAK</strong> kepada <strong>PIHAK</strong> lainnya dalam pelaksanaan Kesepakatan dilakukan secara tertulis dan disampaikan langsung atau melalui pos tercatat dan dialamatkan kepada:</p>\r\n<ol style=\"text-align: justify; list-style-type: upper-roman;\">\r\n<li style=\"line-height: 1.2;\"><strong> </strong>UNIVERSITAS PERTAMINA</li>\r\n</ol>\r\n<p style=\"text-align: justify; padding-left: 40px; line-height: 1.1;\">Kepala Lembaga Penelitian, Pengabdian Masyarakat dan Inovasi</p>\r\n<p style=\"text-align: justify; padding-left: 40px; line-height: 1.1;\">Alamat &nbsp;&nbsp;&nbsp; : Gedung Rektorat, Kawasan Universitas Pertamina Jalan Teuku Nyak Arief, Simprug, Kebayoran Lama, Jakarta Selatan 12220</p>\r\n<p style=\"text-align: justify; padding-left: 40px; line-height: 1.1;\">Telp&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : 021-29643900</p>\r\n<p style=\"text-align: justify; padding-left: 40px; line-height: 1.1;\">Email&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : lppmi@universitaspertamina.ac.id</p>\r\n<ol style=\"text-align: justify; list-style-type: upper-roman;\">\r\n<li>SMA N&nbsp; 8 JAKARTA</li>\r\n</ol>\r\n<p style=\"text-align: justify; padding-left: 40px; line-height: 1.1;\">Kepala Lembaga Penelitian, Pengabdian Masyarakat dan Inovasi</p>\r\n<p style=\"text-align: justify; padding-left: 40px; line-height: 1.1;\">Alamat &nbsp;&nbsp;&nbsp; : Gedung Rektorat, Kawasan Universitas Pertamina Jalan Teuku Nyak Arief, Simprug, Kebayoran Lama, Jakarta Selatan 12220</p>\r\n<p style=\"text-align: justify; padding-left: 40px; line-height: 1.1;\">Telp&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : 021-29643900</p>\r\n<p style=\"text-align: justify; padding-left: 40px; line-height: 1.1;\">Email&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : lppmi@universitaspertamina.ac.id</p>\r\n<p style=\"text-align: center; line-height: 1.1;\"><!-- pagebreak --></p>\r\n<p style=\"text-align: center; line-height: 1.1;\"><strong>Pasal </strong><strong>11</strong></p>\r\n<p style=\"text-align: center; line-height: 1.1;\"><strong>KETENTUAN </strong><strong>LAIN</strong><strong>-</strong><strong>LAIN</strong></p>\r\n<ul style=\"text-align: justify;\">\r\n<li>Dalam hal salah satu <strong>PIHAK</strong> tidak melaksanakan kewajiban sebagaimana dimaksud dalam Pasal 3 (tiga), maka <strong>PIHAK</strong> lain dapat mengakhiri perjanjian ini.</li>\r\n<li>Dalam hal diperlukan adanya penambahan dan/atau pengurangan materi perjanjian, dapat dilakukan perubahan atas persetujuan <strong>PARA PIHAK</strong> yang dituangkan dalam adendum yang merupakan bagian tidak terpisahkan dari perjanjian ini.</li>\r\n<li>Ketentuan yang bersifat teknis dan operasional dalam pelaksanaan perjanjian ini mengacu kepada petunjuk teknis yang disepakati oleh <strong>PARA PIHAK</strong> dan merupakan bagian tidak terpisahkan dari perjanjian ini.</li>\r\n</ul>\r\n<p style=\"line-height: 1.1; text-align: center;\"><!-- pagebreak --></p>\r\n<p style=\"line-height: 1.1; text-align: center;\"><strong>&nbsp;</strong><strong>&nbsp;</strong><strong>Pasal </strong><strong>12</strong></p>\r\n<p style=\"text-align: center; line-height: 1.1;\"><strong>PENUTUP</strong></p>\r\n<p style=\"text-align: justify; line-height: 1.1;\">Demikian perjanjian ini dibuat dengan itikad baik, dalam rangkap dua, bermeterai cukup, memiliki kekuatan hukum yang sama bagi <strong>PARA PIHAK</strong> dan ditandatangani serta dilaksanakan pada tempat dan waktu sebagaimana dinyatakan pada awal perjanjian ini.</p>',	'1cce4fcb-39e4-4afc-bd27-7be71a6a01e5',	'e1ae8fe1-a207-4336-9ea1-ef98ac61cc3f',	'2023-09-07',	'0',	'1',	NULL,	'2021-09-07 20:19:44',	'2021-09-07 20:49:12',	'217',	'217',	'347323d3-ea1a-44f4-9718-fe388e786250',	87,	'PT GANESHCOM MSG',	'/app/lampiran-surat/logo-mitra-20210907201944-LY5PNZ.png',	'2021/22/212/222',	'Gede Doni Agustina',	'Direktur',	NULL,	'0',	'0',	'0',	NULL,	NULL),
